// Crear marcadores y guardarlos en objeto
const marcadores = {};

clientes.forEach(cliente => {
  const marker = L.marker(cliente.posicion).addTo(map);

  // Popup con info, imagen y botón
  marker.bindPopup(`
    <h3>${cliente.nombre}</h3>
    <p><b>Horario:</b> ${cliente.horario}</p>
    <p>${cliente.descripcion}</p>
    <img src="${cliente.imagen}" width="200" style="border-radius:8px;"/><br><br>
    <a href="https://www.google.com/maps/dir/?api=1&destination=${cliente.posicion[0]},${cliente.posicion[1]}" 
       target="_blank">
      <button style="padding:10px;background:#007bff;color:white;border:none;border-radius:5px;cursor:pointer;">
        📍 Llegar
      </button>
    </a>
  `);

  marcadores[cliente.nombre.toLowerCase()] = marker;
});

// 🔎 Buscador
document.getElementById("buscador").addEventListener("input", function() {
  const query = this.value.toLowerCase();
  const clienteEncontrado = clientes.find(c => c.nombre.toLowerCase().includes(query));

  if (clienteEncontrado) {
    map.setView(clienteEncontrado.posicion, 16);
    marcadores[clienteEncontrado.nombre.toLowerCase()].openPopup();
  }
});









